mapper.insert:
insert into recipe_content (
	r_code,
	uuid,
	content
) values (
	#{r_code},
	(select uuid from user_info where mid=#{mid}),
	#{content}
)

mapper.select:
select * from ( select rownum RNUM, zxcvb.* from (
select r.r_code as "R_CODE", r.title as "TITLE", r.uuid as "UUID", r.recommend as "RECOMMEND", rc.content as "CONTENT" from recipe r, recipe_content rc where r.r_code = rc.r_code
) zxcvb) where RNUM >= 1 and RNUM <= 30
