mapper.insert:
insert into notice (
	id,
	title,
	content
) values (
	(select (nvl(max(id),0)+1) from notice),
	#{title},
	#{content}
)

mapper.select:
select * from ( select rownum RNUM, zxcvb.* from (
select id as "ID", title as "TITLE", content as "CONTENT", to_char(create_time,'YYYY-MM-DD HH24:MI') as "CREATE_TIME" from notice order by id desc
) zxcvb) where RNUM >= ${begin_rowNo} and RNUM <= ${end_rowNo}
